mapper.select:
select distinct o.merchant_no from OMS_ORDER o
where
o.PAYMENT_TIME >= #{startDate,jdbcType=TIMESTAMP}
and
o.PAYMENT_TIME < #{endDate,jdbcType=TIMESTAMP}

  and o.MERCHANT_NO = #{merchantNo,jdbcType=DECIMAL}

select
  sum(nvl(COUPON_AMOUNT,0)) as couponAmount
from
  OMS_ORDER
where
 PAYMENT_TIME >= #{startDate,jdbcType=TIMESTAMP}
and
 PAYMENT_TIME < #{endDate,jdbcType=TIMESTAMP}
and
 COUPON_TYPE = #{couponType,jdbcType=DECIMAL}
and
 MERCHANT_NO = #{merchantNo,jdbcType=DECIMAL}

 select
  sum(nvl(ORDER_INTEGRATION,0)) as orderIntegration
from
  OMS_ORDER
where
 PAYMENT_TIME >= #{startDate,jdbcType=TIMESTAMP}
and
 PAYMENT_TIME < #{endDate,jdbcType=TIMESTAMP}
and
 MERCHANT_NO = #{merchantNo,jdbcType=DECIMAL}

select distinct
o.merchant_no as merchantNo
from OMS_ORDER o
where
o.PAYMENT_TIME >= #{startDate,jdbcType=TIMESTAMP}
and
o.PAYMENT_TIME < #{endDate,jdbcType=TIMESTAMP}

  and o.MERCHANT_NO = #{merchantNo,jdbcType=DECIMAL}


  and o.COUPON_TYPE = #{couponType,jdbcType=DECIMAL}

group by o.merchant_no order by o.merchant_no

select distinct o.merchant_no as merchantNo, o.COUPON_TYPE as couponType, s.batch_no as batchNo from OMS_ORDER o left join sms_user_coupon s on o.user_coupon_id = s.user_coupon_id where o.PAYMENT_TIME >= #{startDate,jdbcType=TIMESTAMP} and o.PAYMENT_TIME < #{endDate,jdbcType=TIMESTAMP}
and o.MERCHANT_NO = #{merchantNo,jdbcType=DECIMAL}
and s.batch_no is not null
