mapper.select:
select 

from PMS_SPECIFICATION_ATTRIBUTE
where ID = #{id,jdbcType=DECIMAL}

and MERCHANT_NO =#{merchantNo,jdbcType=DECIMAL}

mapper.insert:
insert into PMS_SPECIFICATION_ATTRIBUTE (ID, MERCHANT_NO, PARENT_ID, 
  ATTRIBUTE_NAME, ATTRIBUTE_VALUE, CREATE_TIME, 
  UPDATE_TIME, CREATED_BY, UPDATED_BY
  )
values (#{id,jdbcType=DECIMAL}, #{merchantNo,jdbcType=DECIMAL}, #{parentId,jdbcType=DECIMAL}, 
  #{attributeName,jdbcType=VARCHAR}, #{attributeValue,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{createdBy,jdbcType=VARCHAR}, #{updatedBy,jdbcType=VARCHAR}
  )

mapper.select:
select
  ID,
  STATUS
from
  PMS_SPECIFICATION_ATTRIBUTE


    and MERCHANT_NO = #{merchantNo,jdbcType=DECIMAL}


    and PARENT_ID = #{parentId,jdbcType=DECIMAL}


    and ATTRIBUTE_NAME = #{attributeName,jdbcType=VARCHAR}

select count(1) from PMS_SPECIFICATION_ATTRIBUTE
PARENT_ID != 0 and STATUS = 1
and MERCHANT_NO = #{merchantNo,jdbcType=DECIMAL}
and PARENT_ID = #{parentId,jdbcType=DECIMAL}

select ATTRIBUTE_NAME from PMS_SPECIFICATION_ATTRIBUTE
PARENT_ID != 0 and STATUS = 1
and MERCHANT_NO = #{merchantNo,jdbcType=DECIMAL}
and PARENT_ID = #{parentId,jdbcType=DECIMAL}
